mapper.insert:
INSERT INTO FOOD_COMMENT (
FOOD_COMMENT_ID
, FOOD_CODE
, MEMBER_ID
, CONTENT
)
VALUES (
(SELECT 'COMMT_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(FOOD_COMMENT_ID, 5))),0) + 1, 3, '0')
FROM FOOD_COMMENT)
, #{foodCode}
, #{memberId}
, #{content}
)

mapper.update:
UPDATE FOOD_COMMENT
SET
CONTENT = #{content}
WHERE FOOD_COMMENT_ID = #{foodCommentId}
