mapper.select:
SELECT ANN_NUM , ANN_CATE , CATE_NAME , ANN_WRITER , ANN_TITLE , ANN_CONTENT , TO_CHAR(ANN_DATE,'YYYY-MM-DD') AS ANN_DATE FROM ANNOUNCEMENT ANN, ANN_CATE_LIST LIST WHERE ANN.ANN_CATE = LIST.CATE_CODE
AND ANN_CATE = #{annCate}
ORDER BY ANN_NUM DESC OFFSET (#{nowPage} -1) * #{displayDataCnt} ROWS FETCH FIRST #{displayDataCnt} ROWS ONLY

mapper.insert:
INSERT INTO ANNOUNCEMENT (
ANN_NUM
, ANN_CATE
, ANN_TITLE
, ANN_CONTENT
)
VALUES (
(SELECT 'ANN_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ANN_NUM, 5))),0) + 1, 3, '0')
FROM ANNOUNCEMENT)
, #{annCate}
, #{annTitle}
, #{annContent}
)

mapper.update:
UPDATE ANNOUNCEMENT
SET
ANN_CATE = #{annCate}
, ANN_TITLE = #{annTitle}
, ANN_CONTENT = #{annContent}
WHERE ANN_NUM = #{annNum}
