mapper.insert:
INSERT INTO DISH_MEMBER (
MEMBER_ID
, MEMBER_PW
, MEMBER_NAME
, MEMBER_TEL
, MEMBER_GENDER
) VALUES (
#{memberId}
, #{memberPw}
, #{memberName}
, #{memberTel}
, #{memberGender}
)

mapper.select:
SELECT MEMBER_ID
FROM DISH_MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT MEMBER_ID
FROM DISH_MEMBER
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_TEL = #{memberTel}

SELECT MEMBER_ID
FROM DISH_MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_TEL = #{memberTel}

mapper.update:
UPDATE DISH_MEMBER
SET ALAM_CNT = ALAM_CNT + 1;
WHERE MEMBER_ID = {memberId}

UPDATE DISH_MEMBER
SET ALAM_CNT = ALAM_CNT - 1;
WHERE MEMBER_ID = {memberId}
