mapper.insert:
INSERT INTO FAVORITE (
MEMBER_ID
, FOOD_CODE

) VALUES (
#{memberId}
, #{foodCode}
)

mapper.select:
SELECT FA.FOOD_CODE , FA.MEMBER_ID , FO.FOOD_TTL , FO.FOOD_NAME , FO.FOOD_DIF , FO.FOOD_INQ_CNT , FO.FOOD_RCMM_CNT , TO_CHAR(FAVORITE_DATE,'YYYY-MM-DD') AS FAVORITE_DATE FROM FAVORITE FA, FOOD FO WHERE FA.MEMBER_ID = #{memberId}
AND FOOD_TTL LIKE '%' || #{searchFavoriteValue} || '%'
AND FA.FOOD_CODE = FO.FOOD_CODE ORDER BY FA.FAVORITE_DATE DESC OFFSET ((#{nowPage} - 1) * #{displayDataCnt8}) ROWS FETCH FIRST #{displayDataCnt8} ROWS ONLY

SELECT FOOD_CODE
FROM FAVORITE
WHERE MEMBER_ID = #{memberId}

mapper.delete:
DELETE FROM FAVORITE
WHERE MEMBER_ID = #{memberId}
AND FOOD_CODE = #{foodCode}

mapper.select:
UPDATE FOOD
SET FOOD_RCMM_CNT =  FOOD_RCMM_CNT + 1
WHERE FOOD_CODE = #{foodCode}

UPDATE FOOD
SET FOOD_RCMM_CNT =  FOOD_RCMM_CNT - 1
WHERE FOOD_CODE = #{foodCode}
